// DataProcessor.h
#include <thread>
#include <mutex>
#include <vector>
#include <memory>
#include <unordered_map>
#include <queue>
#include <condition_variable>

struct MarketData {
    std::string symbol;
    double price;
    int64_t timestamp;
    int volume;

    MarketData(const std::string& raw_data) {
        // Parse raw market data string
        // ... parsing logic
    }

    MarketData(const MarketData& other) : symbol(other.symbol), price(other.price), 
                                         timestamp(other.timestamp), volume(other.volume) {}
};

class DataProcessor {
private:
    static const int NUM_WORKERS = 8;
    std::mutex data_mutex;
    std::condition_variable data_cv;
    std::queue<std::unique_ptr<MarketData>> pending_data;
    std::thread* worker_threads[NUM_WORKERS];
    volatile bool shutdown_flag;

public:
    DataProcessor() : shutdown_flag(false) {
        // Start worker threads
        for(int i = 0; i < NUM_WORKERS; i++) {
            worker_threads[i] = new std::thread(&DataProcessor::process_worker, this, i);
        }
    }

    ~DataProcessor() {
        shutdown_flag = true;
        data_cv.notify_all();

        for(int i = 0; i < NUM_WORKERS; i++) {
            if(worker_threads[i]->joinable()) {
                worker_threads[i]->join();
            }
            delete worker_threads[i];
        }
    }

    void add_data(const std::string& raw_data) {
        auto data = std::make_unique<MarketData>(raw_data);
        {
            std::lock_guard<std::mutex> lock(data_mutex);
            pending_data.push(std::move(data));
        }
        data_cv.notify_one();
    }

    void process_worker(int thread_id) {
        while(!shutdown_flag) {
            std::unique_ptr<MarketData> data;

            {
                std::unique_lock<std::mutex> lock(data_mutex);
                data_cv.wait(lock, [this] { return !pending_data.empty() || shutdown_flag; });

                if(shutdown_flag && pending_data.empty()) {
                    break;
                }

                if(!pending_data.empty()) {
                    data = std::move(pending_data.front());
                    pending_data.pop();
                }
            }

            if(data) {
                // Process the market data
                process_market_data(data.get());

                // Cache the processed result
                cache_result(thread_id, data.get());

                // Notify subscribers
                notify_subscribers(data.get());
            }
        }
    }

    void process_market_data(MarketData* data) {
        // Simulate complex processing that takes time
        std::this_thread::sleep_for(std::chrono::microseconds(100));

        // Update internal calculations
        data->price *= 1.0001; // Apply some transformation
    }
};

// Cache.cpp - Global caching system
static std::unordered_map<int, std::vector<MarketData*>> thread_cache;
static std::unordered_map<std::string, MarketData*> symbol_cache;
static std::mutex cache_mutex;
static std::mutex symbol_mutex;

void cache_result(int thread_id, MarketData* data) {
    std::lock_guard<std::mutex> lock(cache_mutex);

    // Create a copy for thread-specific cache
    MarketData* cached_data = new MarketData(*data);
    thread_cache[thread_id].push_back(cached_data);

    // Also cache by symbol for quick lookups
    {
        std::lock_guard<std::mutex> symbol_lock(symbol_mutex);
        auto existing = symbol_cache.find(data->symbol);
        if(existing != symbol_cache.end()) {
            delete existing->second; // Replace old entry
        }
        symbol_cache[data->symbol] = new MarketData(*data);
    }

    // Cleanup old entries if thread cache grows too large
    if(thread_cache[thread_id].size() > 10000) {
        auto oldest = thread_cache[thread_id].begin();
        delete *oldest;
        thread_cache[thread_id].erase(oldest);
    }
}

void clear_thread_cache(int thread_id) {
    std::lock_guard<std::mutex> lock(cache_mutex);
    for(auto* data : thread_cache[thread_id]) {
        delete data;
    }
    thread_cache[thread_id].clear();
}

// Subscription system
static std::vector<std::function<void(const MarketData&)>> subscribers;
static std::mutex subscriber_mutex;

void notify_subscribers(MarketData* data) {
    std::vector<std::function<void(const MarketData&)>> local_subscribers;
    {
        std::lock_guard<std::mutex> lock(subscriber_mutex);
        local_subscribers = subscribers; // Copy the subscribers
    }

    // Notify each subscriber
    for(auto& callback : local_subscribers) {
        callback(*data);
    }
}

void add_subscriber(std::function<void(const MarketData&)> callback) {
    std::lock_guard<std::mutex> lock(subscriber_mutex);
    subscribers.push_back(callback);
}

