                    // Mark as deleted first (logical deletion)
                    bool expected_deleted = false;
                    if (current->deleted.compare_exchange_strong(
                        expected_deleted, true, std::memory_order_acq_rel)) {

                        size_.fetch_sub(1, std::memory_order_acq_rel);

                        // Try physical deletion
                        Node* next_node = current->next.load(std::memory_order_acquire);

                        if (prev == nullptr) {
                            // Removing head of bucket
                            if (buckets[bucket_index].compare_exchange_strong(
                                current, next_node, std::memory_order_acq_rel)) {
                                delete current;
                            }
                            // If CAS fails, leave for cleanup thread
                        } else {
                            // Removing middle/end of chain
                            if (prev->next.compare_exchange_strong(
                                current, next_node, std::memory_order_acq_rel)) {
                                delete current;
                            }
                            // If CAS fails, leave for cleanup thread
                        }

                        return true;
                    } else {
                        // Already deleted by another thread
                        return false;
                    }
                }
                prev = current;
                current = current->next.load(std::memory_order_acquire);
            }

            return false; // Key not found
        }
    }

    size_t size() const {
        return size_.load(std::memory_order_acquire);
    }

private:
    void resize() {
        static std::atomic<bool> resize_in_progress(false);

        // Try to acquire resize lock
        bool expected = false;
        if (!resize_in_progress.compare_exchange_strong(
            expected, true, std::memory_order_acq_rel)) {
            // Another thread is already resizing
            return;
        }

        // Double-check if resize is still needed
        size_t current_size = size_.load(std::memory_order_acquire);
        size_t current_capacity = capacity_.load(std::memory_order_acquire);

        if (current_size < current_capacity * MAX_LOAD_FACTOR) {
            resize_in_progress.store(false, std::memory_order_release);
            return;
        }

        size_t new_capacity = current_capacity * 2;
        std::atomic<Node*>* new_buckets = new std::atomic<Node*>[new_capacity];

        for (size_t i = 0; i < new_capacity; ++i) {
            new_buckets[i].store(nullptr, std::memory_order_relaxed);
        }

        // Rehash all existing nodes
        for (size_t i = 0; i < current_capacity; ++i) {
            Node* current = buckets[i].load(std::memory_order_acquire);

            while (current != nullptr) {
                Node* next = current->next.load(std::memory_order_acquire);

                if (!current->deleted.load(std::memory_order_acquire)) {
                    // Rehash to new bucket
                    K key = current->key.load(std::memory_order_acquire);
                    size_t new_bucket = hasher(key) % new_capacity;

                    Node* new_head = new_buckets[new_bucket].load(std::memory_order_acquire);
                    current->next.store(new_head, std::memory_order_relaxed);

                    // This CAS might fail if another rehashing thread beats us
                    new_buckets[new_bucket].compare_exchange_weak(
                        new_head, current, std::memory_order_release, std::memory_order_acquire);
                } else {
                    // Clean up deleted nodes during rehash
                    delete current;
                }

                current = next;
            }
        }

        // Atomically switch to new bucket array
        std::atomic<Node*>* old_buckets = buckets;
        buckets = new_buckets;
        capacity_.store(new_capacity, std::memory_order_release);

        // Increment version to help with ABA problem
        version_counter.fetch_add(1, std::memory_order_acq_rel);

        // Clean up old bucket array (but not the nodes, they're now in new buckets)
        delete[] old_buckets;

        resize_in_progress.store(false, std::memory_order_release);
    }
};

